const removeQuery = () => {
    if (window.history.pushState && window.location.pathname) {
        var newurl =
            window.location.protocol +
            '//' +
            window.location.host +
            window.location.pathname;
            window.history.pushState('', '', newurl);
    } else {
        newurl = 
            window.location.protocol + '//' + 
            window.location.host;
            window.history.pushState('', '', newurl);
    }
};
